void dump_packet(char *data, int len, char *compare)
{
	int i;
	int differ = 0;
	printf("{");
	for (i = 0; i < len; i++) {
		if (i) printf(",");
		printf(" 0x%02x", ((int)data[i]) & 0xff);
		if (compare && compare[i] != data[i])
			differ = 1;
	}
	if (differ)
		printf(" }},\n\n               (DIFFER!!!!!!!!!!!!!)\n\n");
	else
		printf(" }},\n");
}

void action(int socket, enum source src)
{ 
	int i;
	struct cmd_t *cmd;
	int status;
	char buf[1024];

	for (i = 0; i < sizeof(cmds)/sizeof(struct cmd_t); i++)
	{
		cmd = &cmds[i];
		if (src ^ cmd->dir) {
			/* send data */
			status = write(socket, cmd->data, cmd->len);
			if (status < 0) {
				perror("Send");
				return;
			}
			printf("sent %d bytes:", status);
			dump_packet(cmd->data, status, NULL);
		} else {
			/* receive data */
			status = read(socket, buf, sizeof(buf));
			if (status < 0)
			{
				perror("Receive");
				return;
			}
			printf("received %d bytes:", status);
			dump_packet(buf, status, cmd->data);
		}
	}

}
